    abstract class Monster      //추상클래스 Monster 생성
    {
        enum MonsterSKillList
        {
            Goblin,Throw_Rock,

            Ghoul,Rotting_Touch,Bite, 

            SnowMan, Blizzard,Throw_SnowBall,
            
            Golem, Rock_Smash ,Stone_Skin ,

            Dragon,Fire_Breath, Wing_Sweep, Tail_Slam,

