        protected int MonsterHp;
        protected int MonsterMp;
        protected int MonsterPower;
        protected int MonsterDefense;
       
   
        public abstract void MonsterReward();   //추상메서드 생성
        public abstract void BattlePlayer(Player _player);
        public abstract void MonsterSkill();

        TextVector textVector = new TextVector();   //텍스트 출력 좌표지정

        public void PrintMonsterText(string text)       //몬스터 대사 출력
        {
            
            textVector.x = 80;
            textVector.y = 50;

            Console.Clear();
            Console.ResetColor();

            Console.SetCursorPosition(textVector.x / 2 - text.Length, textVector.y / 2 - 3);
            Console.Write(text);
            Console.SetCursorPosition(0, 0);
            Thread.Sleep(1000); 

        }
    }

